%Uncomment 20, 64, 68 and 71 and comment out 21, 65, 69 and 72
% if linear SA model is being used and viceversa for ES only
% Path information
Home = getenv('HOME');
addpath(sprintf('%s/repos/pointbem',Home));
addpath(sprintf('%s/repos/panelbem',Home));
addpath(sprintf('%s/repos/testasymmetry',Home));
addpath(sprintf('%s/repos/testasymmetry/functions',Home));
addpath(sprintf('%s/repos/testasymmetry/mobley',Home));
addpath(sprintf('%s/repos/testasymmetry/born',Home));

% a bunch of useful variables and constants. also defining the global
% variable "ProblemSet" which we'll use to hold the BEM systems.
loadConstants
convertKJtoKcal = 1/joulesPerCalorie;
global UsefulConstants ProblemSet saveMemory writeLogfile logfileName
logfileName = 'water.out';
epsOut = 78.36; %from MNsol

%ParamWatInfo = load('OptWaterSA');
ParamWatInfo = load('OptWater_es');
x = ParamWatInfo.x;

fid = fopen('mnsol/water_es.csv','r'); 
Data = textscan(fid,'%s %f','delimiter',',');
fclose(fid);
mol_list = Data{1};
dG_list = Data{2};

fid = fopen('mnsol/mobley_sa_Chris.csv','r');
Data = textscan(fid,'%s %f %f %f %f %f %f','delimiter',',');
fclose(fid);
all_solutes = Data{1};
all_surfAreas = Data{2};
es_mobley = Data{6};
np_mobley = Data{7};
[m, index] = ismember(mol_list,all_solutes);
surfArea_list = all_surfAreas(index);
saveMemory = 1;
writeLogfile = 1;
epsIn  =  1;
KelvinOffset = 273.15;
conv_factor = 332.112;
staticpotential = 0.0; % this only affects charged molecules;

kappa = 0.0;  % should be zero, meaning non-ionic solutions!
UsefulConstants = struct('epsIn',epsIn,'epsOut',epsOut,'kappa', ...
			 kappa,'conv_factor',conv_factor,...
			 'staticpotential',staticpotential);

% here we define the actual params for the NLBC test

curdir = pwd;
for i=1:length(mol_list)
  dir=sprintf('%s/Dropbox/lab/projects/slic-jctc-mnsol/nlbc-mobley/nlbc_test/%s',getenv('HOME'),mol_list{i});
  chdir(dir);
  pqrData = loadPqr('test.pqr');
  pqrAll{i} = pqrData;
  srfFile{i} = sprintf('%s/test_2.srf',dir);
  chargeDist{i} = pqrData.q;
  referenceData{i} = dG_list(i);
  surfArea{i} = surfArea_list(i);
  chdir(curdir);
  %addProblemSA(mol_list{i},pqrAll{i},srfFile{i},chargeDist{i},referenceData{i},surfArea{i});
  addProblem(mol_list{i},pqrAll{i},srfFile{i},chargeDist{i},referenceData{i});
end

%[err,calcE,refE,es,np]=ObjectiveFromBEMSA(x);
err=ObjectiveFromBEM(x);
rmse = rms(err);
%save('RunWaterSA','err','calcE','refE','es','np','rmse');
save('RunWater_es','err','rmse');
